resources.string:
{
    user {
        ...UserInfos

        friends {
            ...UserInfos
        }

        groups {
            name
        }

        reported {
            ...UserInfos
        }
    }
}

fragment UserInfos on User {
    id
    displayName
    profilePicture
}

mutation {
    newTribe(
        tribe: {
            from: \"%1$s\",
            to: \"%2$s\",
            to_group: %3$s,
            type: \"%4$s\",
            recorded_at: \"%5$s\",
            lat: %6$s,
            lng: %7$s
        }
    ) { id, recorded_at }
}

{
    user {
        tribes {
            id
            to
            to_group
            type url
            recorded_at
            lat
            lng
        }
    }
}
