resources.string:
{
    user(id: \"%1$s\") {
        ...UserInfos

        friends {
            ...UserInfos
        }

        groups {
            name
            members {
                ...UserInfos
            }
        }

        reported {
            ...UserInfos
        }

        installs {
            gcm_id
            user {
                ...UserInfos
            }
        }
    }
}

fragment UserInfos on User {
    id
    displayName
    profilePicture
}
