resources.string:
{
    user {
        ...UserInfos

        friendships {
            id
            tag
            blocked
            category
            friend {
                ...UserInfos
            }
        }

        groups {
            id
            name
            picture
            created_at
            updated_at
        }
    }
}

fragment UserInfos on User {
    id
    username
    displayName
    profilePicture
    created_at
    updated_at
}

{
    users (ids: [%1$s]) {
        ...UserInfos
    }
}

fragment UserInfos on User {
    id
    username
    displayName
    profilePicture
    created_at
    updated_at
}

mutation {
    newTribe(
        tribe: {
            from: \"%1$s\",
            to: \"%2$s\",
            to_group: %3$s,
            type: \"%4$s\",
            recorded_at: \"%5$s\",
            lat: %6$s,
            lng: %7$s
        }
    ) { id, recorded_at }
}

mutation {
    newMessage(
        message: {
            from: \"%1$s\",
            to: \"%2$s\",
            to_group: %3$s,
            type: \"%4$s\",
            recorded_at: \"%5$s\",
            content : \"%6$s\"
        }
    ) { id, created_at, recorded_at }
}

{
    user {
        tribes {
            id
            from
            to
            to_group
            type url
            recorded_at
            lat
            lng
            weather {
                temp_f
                temp_c
                icon
            }
            location {
                city
            }
        }

        unSeenMessages {
            id
            from
            to
            to_group
            type
            content
            recorded_at
            created_at
        }
    }

    %1$s
}
